mapper.select:
SELECT 

FROM live_channel_files a

WHERE a.id = #{id}

SELECT
FROM live_channel_files a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.name LIKE '%'||#{name}||'%' '%'+#{name}+'%' concat('%',#{name},'%')
AND a.live_channel_id = #{liveChannel.id}
AND a.file_path LIKE '%'||#{filePath}||'%' '%'+#{filePath}+'%' concat('%',#{filePath},'%')
AND a.download_url LIKE '%'||#{downloadUrl}||'%' '%'+#{downloadUrl}+'%' concat('%',#{downloadUrl},'%')
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT 

FROM live_channel_files a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE live_channel_files SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
