mapper.select:
SELECT 

FROM live_live_channel a

WHERE a.id = #{id}

SELECT

FROM live_live_channel a

WHERE a.rel_id = #{relId}
and a.business_system_id =#{businessSystem.id}

SELECT 

FROM live_live_channel a

WHERE a.channel_secret_key = #{channelSecretKey}

SELECT 

FROM live_live_channel a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE live_live_channel SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
