mapper.select:
SELECT 

FROM meeting_chinanet_live_channel a

WHERE a.id = #{id}

SELECT 

FROM meeting_chinanet_live_channel a

WHERE a.rel_id = #{relId}

SELECT
FROM meeting_chinanet_live_channel a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.name LIKE '%'||#{name}||'%' '%'+#{name}+'%' concat('%',#{name},'%')
AND a.rel_id LIKE '%'||#{relId}||'%' '%'+#{relId}+'%' concat('%',#{relId},'%')
AND a.transcoding_paras LIKE '%'||#{transcodingParas}||'%' '%'+#{transcodingParas}+'%' concat('%',#{transcodingParas},'%')
AND a.status = #{status}
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT 

FROM meeting_chinanet_live_channel a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE meeting_chinanet_live_channel SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
