mapper.select:
SELECT 

FROM meeting_join_user a

WHERE a.id = #{id}

SELECT 

FROM meeting_join_user a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.name LIKE 
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')


		AND a.ip = #{ip}


		AND a.start_time = #{startTime}


		AND a.end_time = #{endTime}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM meeting_join_user a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE meeting_join_user SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
