mapper.select:
SELECT 

FROM meeting_live_channel a

WHERE a.id = #{id}

SELECT 

FROM meeting_live_channel a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.name LIKE 
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')


		AND a.logo = #{logo}


		AND a.rtmp_hd = #{rtmpHd}


		AND a.rtmp_sd = #{rtmpSd}


		AND a.rtmp_nd = #{rtmpNd}


		AND a.rtmp_vn = #{rtmpVn}


		AND a.hls_hd = #{hlsHd}


		AND a.hls_sd = #{hlsSd}


		AND a.hls_nd = #{hlsNd}


		AND a.hls_vn = #{hlsVn}


		AND a.status = #{status}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM meeting_live_channel a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE meeting_live_channel SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
