mapper.select:
SELECT 

FROM meeting_meeting_info a

WHERE a.id = #{id}

SELECT 

FROM meeting_meeting_info a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.name LIKE 
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')


		AND a.present = #{present}


		AND a.type = #{type}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM meeting_meeting_info a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE meeting_meeting_info SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
