mapper.select:
SELECT 

FROM meeting_ppt_operation_records a

WHERE a.id = #{id}

SELECT 

FROM meeting_ppt_operation_records a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.name LIKE 
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')


		AND a.ip = #{ip}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM meeting_ppt_operation_records a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE meeting_ppt_operation_records SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
