mapper.select:
SELECT 

FROM sys_uploadfile a



		AND a.id = #{id}


		AND a.filename = #{filename}


		AND a.filesize = #{filesize}


		AND a.projectId = #{project.id}

SELECT 

FROM sys_uploadfile a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.filename LIKE 
			'%'||#{filename}||'%'
			'%'+#{filename}+'%'
			concat('%',#{filename},'%')


		AND a.projectId = #{project.id}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM sys_uploadfile a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE sys_uploadfile SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
