mapper.select:
SELECT 

FROM t_classroom a

WHERE a.id = #{id}

SELECT 

FROM t_classroom a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.classnumber = #{classnumber}


		AND a.rname = #{rname}


		AND a.position = #{position}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM t_classroom a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE t_classroom SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
