mapper.select:
SELECT 

FROM t_config a

WHERE a.id = #{id}

SELECT 

FROM t_config a


		a.config_key = #{configkey}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT
FROM t_config a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.config_explain LIKE '%'||#{configExplain}||'%' '%'+#{configExplain}+'%' concat('%',#{configExplain},'%')
AND a.config_value LIKE '%'||#{configValue}||'%' '%'+#{configValue}+'%' concat('%',#{configValue},'%')
AND a.config_key = #{configkey}
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT 

FROM t_config a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE t_config SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
