mapper.select:
SELECT 

FROM t_liveing a

WHERE a.id = #{id}

SELECT
FROM t_liveing a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.livename LIKE '%'||#{livename}||'%' '%'+#{livename}+'%' concat('%',#{livename},'%')
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT 

FROM t_liveing a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE t_liveing SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
