mapper.select:
SELECT 

FROM t_nodegroup a

WHERE a.id = #{id}

SELECT 

FROM t_nodegroup a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.nodeid = #{nodeid}


		AND a.groupid = #{groupid}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM t_nodegroup a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE t_nodegroup SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
