mapper.select:
SELECT 

FROM t_nodeserver a

WHERE a.id = #{id}

SELECT
FROM t_nodeserver a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.name LIKE '%'||#{name}||'%' '%'+#{name}+'%' concat('%',#{name},'%')
AND a.online_flag = #{onlineFlag}
AND a.ipaddress LIKE '%'||#{ipaddress}||'%' '%'+#{ipaddress}+'%' concat('%',#{ipaddress},'%')
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT 

FROM t_nodeserver a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE t_nodeserver SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
