FROM t_nodeservergroup a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.groupname LIKE '%'||#{groupname}||'%' '%'+#{groupname}+'%' concat('%',#{groupname},'%')
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT 

FROM t_nodeservergroup a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

