mapper.select:
SELECT 

FROM t_programgroup a

WHERE a.groupid = #{groupId}
	AND
	  a.programid = #{programId}

SELECT 

FROM t_programgroup a

WHERE a.id = #{id}

SELECT 

FROM t_programgroup a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM t_programgroup a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE t_programgroup SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
