mapper.select:
SELECT 

FROM t_softupdate a

WHERE a.id = #{id}

SELECT
FROM t_softupdate a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.fileurl LIKE '%'||#{fileurl}||'%' '%'+#{fileurl}+'%' concat('%',#{fileurl},'%')
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT 

FROM t_softupdate a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE t_softupdate SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
