mapper.select:
SELECT 

FROM t_studentgroup a

WHERE a.userid = #{userid}

SELECT 

FROM t_studentgroup a

WHERE a.id = #{id}

SELECT 

FROM t_studentgroup a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.groupid = #{groupid}


		AND a.userid = #{userid}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM t_studentgroup a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE t_studentgroup SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
