mapper.select:
SELECT 

FROM t_template a

WHERE a.id = #{id}

SELECT
FROM t_template a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.name LIKE '%'||#{name}||'%' '%'+#{name}+'%' concat('%',#{name},'%')
AND a.cmd LIKE concat('%',#{cmd},'%')
AND a.format = #{format}
AND a.type LIKE '%'||#{type}||'%' '%'+#{type}+'%' concat('%',#{type},'%')
${sqlMap.dsf}
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT 

FROM t_template a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE t_template SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

